import React, { useState, useEffect } from "react";
import "./Events.css";
import { Link } from "react-router-dom";
import UserProfileModel from "./UserProfileModel";

function Events() {
  const [events, setEvents] = useState([]);
  const [showUserProfileModal, setShowUserProfileModal] = useState(false);
  const [user, setUser] = useState();
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchEvents();
  }, []);

  const openUserProfileModal = () => {
    const userData = JSON.parse(localStorage.getItem("user"));
    setUser(userData);
    setShowUserProfileModal(true);
  };

  const closeUserProfileModal = () => {
    setShowUserProfileModal(false);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  function formatDate(dateString) {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  }

  const fetchEvents = async () => {
    try {
      const response = await fetch("https://ticket-a8ez.onrender.com/event/all", {
        headers: {
          Authorization: `${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();

        const sampleEvents = [
          {
            _id: "1",
            name: "Tech Conference 2025",
            date: "2025-06-15",
            image: "https://source.unsplash.com/600x400/?technology,conference",
            description: "Join industry experts discussing the latest trends in technology.",
          },
          {
            _id: "2",
            name: "Music Fest 2025",
            date: "2025-07-20",
            image: "https://source.unsplash.com/600x400/?concert,music",
            description: "A spectacular music festival featuring top artists from around the world.",
          },
          {
            _id: "3",
            name: "Food Carnival",
            date: "2025-08-10",
            image: "https://source.unsplash.com/600x400/?food,festival",
            description: "Taste delicious cuisines from different cultures in one place.",
          },
          {
            _id: "4",
            name: "Startup Meetup",
            date: "2025-09-05",
            image: "https://source.unsplash.com/600x400/?startup,business",
            description: "Network with entrepreneurs and investors to share innovative ideas.",
          },
          {
            _id: "5",
            name: "Sports Championship",
            date: "2025-10-12",
            image: "https://source.unsplash.com/600x400/?sports,competition",
            description: "Watch thrilling matches and cheer for your favorite teams.",
          },
          {
            _id: "6",
            name: "Art Exhibition",
            date: "2025-11-25",
            image: "https://source.unsplash.com/600x400/?art,exhibition",
            description: "Explore stunning artworks and interact with talented artists.",
          },
          {
            _id: "7",
            name: "Coding Hackathon",
            date: "2025-12-10",
            image: "https://source.unsplash.com/600x400/?coding,hackathon",
            description: "Compete with the best developers to solve challenging problems.",
          },
          {
            _id: "8",
            name: "Gaming Tournament",
            date: "2026-01-15",
            image: "https://source.unsplash.com/600x400/?gaming,esports",
            description: "Battle it out in a high-stakes gaming competition.",
          },
        ];

        setEvents(sampleEvents);
      } else {
        console.error("Failed to fetch events.");
      }
    } catch (error) {
      console.error("Error during fetchEvents:", error);
    }
  };

  return (
    <div className="events">
      {/* Navbar */}
      <nav className="navbar">
        <div className="left">
          <Link to={"/dashboard"}>
            <img src="your-logo.png" alt="Logo" className="logo" />
          </Link>
        </div>
        <div className="right">
          <button className="profile-btn" onClick={toggleDropdown}>Profile â–¼</button>
          {dropdownOpen && (
            <div className="dropdown-menu">
              <button onClick={openUserProfileModal}>View Profile</button>
              <button onClick={() => localStorage.clear()}>Logout</button>
            </div>
          )}
        </div>
      </nav>

      {/* Events Section */}
      <div className="events-container">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event._id} className="event-card">
              <img src={event.image} alt={event.name} className="event-image" />
              <h3>{event.name}</h3>
              <p>{event.description}</p>
              <p>Date: {formatDate(event.date)}</p>
              <button onClick={() => alert(`Booked: ${event.name}`)}>Book Now</button>
            </div>
          ))
        ) : (
          <p>No events available</p>
        )}
      </div>

      {showUserProfileModal && <UserProfileModel user={user} onClose={closeUserProfileModal} />}
    </div>
  );
}

export default Events;
